import React, {useState} from 'react';
import {API_KEY, API_URL, IMAGE_BASE_URL, BACKDROP_SIZE} from '../config';

// importing compomemts that will be shown on our homepage

import Grid from './elements/Grid';
import Header from './elements/Header';
import MoreBtn from './elements/MoreBtn';
import SearchBar from './elements/SearchBar';
import Spinner from './elements/Spinner';
import TopImage from './elements/TopImage';
import VideoThumb from './elements/VideoThumb';

//Using custom hook (useHomeFetch)
import  {useHomeFetch}  from './hooks/useHomeFetch';


const Home = () => {

        const [searchTerm, setSearchTerm] = useState('');
        const [
          {
            state: { movies, currentPage, totalPages, heroImage },
            loading,
            error,
          },
          fetchMovies,
        ] = useHomeFetch(searchTerm);


    return ( <>  
            <TopImage
            image={`${IMAGE_BASE_URL}${BACKDROP_SIZE}${state.TopImage.backdrop_path}`} 
            title={state.TopImage.original_title}
            text={state.TopImage.overview}
            />
            <SearchBar />
            <Grid />
            <VideoThumb />
            <Spinner />
            <MoreBtn />
    </>
    )
}

export default Home;
