import React, {useState, useEffect} from 'react';
import {API_KEY, API_URL} from '../config';

// importing compomemts that will be shown on our homepage

import Grid from './elements/Grid';
import Header from './elements/Header';
import MoreBrn from './elements/MoreBtn';
import SearchBar from './elements/SearchBar';
import Spinner from './elements/Spinner';
import TopImage from './elements/TopImage';
import VideoThumb from './elements/VideoThumb';

const Home = () => {

    const [state, setState] = useState({videos: []});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(false);

    console.log(state);

    const fetchVideos = async endpoint => {
        setError(false);
        setLoading(true);

        try {
            const result = await (await fetch(endpoint)).json();
            console.log(result);
            setState(prev => ({
                ...prev, 
                videos: [...result.results],
                currentPage: result.page, 
                totalPages: result.total_pages,
            })) 
        } catch(error){
            setError(true);
            console.log(error);
        }
        setLoading(false);
    }

    useEffect(() => {
    fetchVideos(`${API_URL}videos?part=contentDetails&chart=mostPopular&maxResults=25&region_Code=RU&key=${API_KEY}`)
    }
    )

    return ( <>

            <TopImage />
            <SearchBar />
            <Grid />
            <VideoThumb />
            <Spinner />
            <MoreBrn />
    </>
    )
}

export default Home;
