import React, {useState} from 'react';
import {API_KEY, API_URL, IMAGE_BASE_URL, BACKDROP_SIZE, POSTER_SIZE} from '../config';

// importing compomemts that will be shown on our homepage

import Grid from './elements/Grid';
import MoreBtn from './elements/MoreBtn';
import SearchBar from './elements/SearchBar';
import Spinner from './elements/Spinner';
import HeroImage from './elements/HeroImage';
import VideoThumb from './elements/VideoThumb';

// custom hook
import  {useHomeFetch}  from './hooks/useHomeFetch';
import noImage from './images/no_image.jpg';


const Home = () => {

   const [{ state: {movies, currentPage, totalPages, heroImage},
    loading, error}, fetchMovies] = useHomeFetch();
   const [searchTerm, setSearchTerm] = useState('');

   const loadMoreMovies = () => {
    const searchEndpoint = `${API_URL}search/movie?api_key=${API_KEY}&query=${searchTerm}&page=${currentPage+1}`;
    const popularEndpoint = `${API_URL}movie/popular?api_key=${API_KEY}&page=${currentPage+1}`;
    const endpoint = searchTerm ? searchEndpoint : popularEndpoint;

    fetchMovies(endpoint);
   }

        if (error) return <div>Something went wrong</div>;
        if (!movies[0]) return <Spinner/>;

    return ( <>  
            <HeroImage
            image={`${IMAGE_BASE_URL}${BACKDROP_SIZE}${state.heroImage.backdrop_path}`} 
            title={state.heroImage.original_title}
            text={state.heroImage.overview}
            />
            <SearchBar />
            <Grid header={searchTerm ? 'Search Result' : 'Popular Movies'}>
                {state.movies.map(movie => (
                    // children of the grid component
                    <VideoThumb
                    key={movie.id}
                    clickable
                    image = {
                        movie.poster_path 
                        ?  `${IMAGE_BASE_URL}${POSTER_SIZE}${movie.poster_path}`
                        : noImage
                    }
                    movieId={movie.id}
                    />
                ))}
            </Grid>
            {loading && <Spinner />}
            <MoreBtn text="Load More" callback={loadMoreMovies}/>
    </>
    )
}

export default Home;
